import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const AdminStudentPage = () => {
    const [courseInfo, setCourseInfo] = useState(null);
    const [studentInfo, setstudentInfo] = useState(null);
    const { courseName } = useParams();

    const [students, setStudents] = useState([]);


/*    useEffect(() => {
        fetchStudents();
    }, []);*/

    useEffect(() => {
        const fetchStudents = async () => {
            try {
                const token = localStorage.getItem('token');
                const config = {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        Accept: 'application/json'
                    }
                };
                const response = await axios.get(`http://localhost:8080/api/v1/users/courseName?courseName=${courseName}`, config);
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setStudents(data);
            } catch (error) {
                console.error('There was a problem with the fetch operation:', error);
            }
        };

        fetchStudents();

        // Cleanup function (optional)
        return () => {
            // Cleanup code here (if needed)
        };
    }, [courseName]); // Run effect when courseName changes



    const fetchStudents = async () => {
        try {
            const token = localStorage.getItem('token');
            const config = {
                headers: {
                    Authorization: `Bearer ${token}`,
                    Accept: 'application/json'
                }
            };
            //http://localhost:8080/api/v1/users/courseName?courseName=Software Engineering
            //http://localhost:8080/api/v1/courses/name?courseName=${courseName}
            const response = await axios.get(`http://localhost:8080/api/v1/users/courseName?courseName=${courseName}`, config);
            setCourseInfo(response.data);
        } catch (error) {
            console.error('Error fetching students:', error);
        }
    };


    return (
        <div>
            <h1>Course: {courseInfo.courseName}</h1>
            <h2>
                Students:
            </h2>
            <ul>
                {students.map((student, index) => (
                    <li key={index}>{student.firstName} {student.lastName}</li>
                ))}
            </ul>
        </div>
    );
};

export default AdminStudentPage;
