import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const AdminStudentPage = () => {
    const [courseInfo, setCourseInfo] = useState(null);
    const [studentInfo, setstudentInfo] = useState(null);
    const { courseName } = useParams();

    useEffect(() => {
        fetchStudents();
    }, []);

    const fetchStudents = async () => {
        try {
            const token = localStorage.getItem('token');
            const config = {
                headers: {
                    Authorization: `Bearer ${token}`,
                    Accept: 'application/json'
                }
            };
            //http://localhost:8080/api/v1/users/courseName?courseName=Software Engineering
            //http://localhost:8080/api/v1/courses/name?courseName=${courseName}
            const response = await axios.get(`http://localhost:8080/api/v1/users/courseName?courseName=${courseName}`, config);
            setCourseInfo(response.data);
        } catch (error) {
            console.error('Error fetching students:', error);
        }
    };

    if (!courseInfo) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>Course: {courseInfo.courseName}</h1>
            <h2>
                Students:
            </h2>
            <ul>
                {courseInfo.assignments.map((assignment, index) => (
                    <li key={index}>{assignment}</li>
                ))}
            </ul>


            {/* Loop through courses by semester if coursesBySemester contains valid data */}
            {Object.entries(studentInfo).map(([semester, courses]) => (
                <div key={semester}>
                    <h3>Courses for {semester}</h3>
                    <ul>
                        {courses.map(course => (
                            <li key={course.id}>
                                <p>Course Name: {course.courseName}</p>
                                {/* Add more course details here */}
                            </li>
                        ))}
                    </ul>
                </div>
            ))}


            <h2>Quizzes:</h2>
            <ul>
                {courseInfo.quizzes.map((quiz, index) => (
                    <li key={index}>{quiz}</li>
                ))}
            </ul>
        </div>
    );
};

export default AdminStudentPage;
