import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const StudentCoursePage = () => {
    const [courseInfo, setCourseInfo] = useState(null);
    const { courseName } = useParams();
    // State to store the courses data fetched from MongoDB
    const [courses, setCourses] = useState([]);
    // State to store the grades for a selected course
    const [selectedCourseGrades, setSelectedCourseGrades] = useState([]);

    useEffect(() => {
        fetchCourseInfo();
    }, []);

    // Fetch course data from MongoDB
    useEffect(() => {
        fetchCourseGrade();
    }, []);

    // Function to fetch grades for a selected course
    const fetchCourseGrades = async (courseId) => {
        try {
            // Assuming you have an API endpoint to fetch grades for a course
            const response = await fetch(`/api/grades?courseId=${courseId}`);
            if (!response.ok) {
                throw new Error('Failed to fetch grades');
            }
            const data = await response.json();
            setSelectedCourseGrades(data);
        } catch (error) {
            console.error('Error fetching grades:', error);
        }
    };

    // Render grades for a selected course
    const renderGrades = () => {
        return selectedCourseGrades.map((grade, index) => (
            <div key={index}>
                <p>Student: {grade.studentName}</p>
                <p>Grade: {grade.grade}</p>
            </div>
        ));
    };

    const fetchCourseInfo = async () => {
        try {
            const token = localStorage.getItem('token');
            const config = {
                headers: {
                    Authorization: `Bearer ${token}`,
                    Accept: 'application/json'
                }
            };
            const response = await axios.get(`http://localhost:8080/api/v1/courses/name?courseName=${courseName}`, config);
            setCourseInfo(response.data);
        } catch (error) {
            console.error('Error fetching course info:', error);
        }
    };

    const fetchCoursesGrade = async () => {
        try {
            const token = localStorage.getItem('token');
            const config = {
                headers: {
                    Authorization: `Bearer ${token}`,
                    Accept: 'application/json'
                }
            };
            const response = await axios.get(`http://localhost:8080/api/v1/courses/name?courseName=${courseName}`, config);
            setCourseInfo(response.data);
        } catch (error) {
            console.error('Error fetching course info:', error);
        }
    };

    if (!courseInfo) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>Course: {courseInfo.courseName}</h1>
            <h2>Assignments:</h2>
            <ul>
                {courseInfo.assignments.map((assignment, index) => (
                    <li key={index}>{assignment}</li>
                ))}
            </ul>
            <h2>Quizzes:</h2>
            <ul>
                {courseInfo.quizzes.map((quiz, index) => (
                    <li key={index}>{quiz}</li>
                ))}
            </ul>

            <ul>
                {courses.map(course => (
                    <li key={course.courseid} onClick={() => fetchCourseGrades(course.courseid)}>
                        {course.courseName}
                    </li>
                ))}
            </ul>
            <h2>Grades</h2>
            <div>
                {selectedCourseGrades.length > 0 ? renderGrades() : <p>Select a course to view grades</p>}
            </div>
        </div>


    );
};

export default StudentCoursePage;
